<?php get_header(); ?><div id="main" <?php post_class('container') ?>>    <div class="row">        <h1>Blog</h1>        <div id="content-blog" class="col-md-7">            <?php            // The Query            $paged = get_query_var('paged') ? get_query_var('paged') : 1;            $args = array(                // 'post_type' => ['colunas', 'post'],                'cat' => '-7,-8',                'posts_per_page' => 5,                'post__not_in' => array($viewPost->ID),                'orderby' => 'date',                'order' => 'DESC',                'paged' => $paged            );            //query_posts('cat=&posts_per_page=5&orderby=date&order=DESC&paged='.$paged.'');            $the_query = new WP_Query( $args );                        // The Loop            while ($the_query->have_posts()) :                 $the_query->the_post();                get_template_part('content', 'index');            endwhile;                        // Reset Query            wp_reset_postdata(); ?>                        <nav>                <?php                 global $wp_query;                $big = 999999999; // need an unlikely integer                                $paginacao = array(                    'base' => str_replace( $big, '%#%', esc_url( get_pagenum_link( $big ) ) ),                    'format' => '?paged=%#%',                    'current' => max( 1, get_query_var('paged') ),                    'total' => $the_query->max_num_pages                );                echo paginate_links( $paginacao ); ?>            </nav>        </div> <!-- #content-blog -->        <section id="sidebar" class="col-md-4 col-md-offset-1">            <?php get_sidebar(); ?>        </section><!-- #sidebar -->    </div><!-- .row--></div><?php get_footer(); ?>